import React, { useState, useEffect, useRef } from 'react';
import { Send, Bot, User, Loader2, MessageSquare, Plus, Trash2, Database } from 'lucide-react';
import axios from 'axios';
import DatabaseViewer from './components/DatabaseViewer';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversationId, setConversationId] = useState(null);
  const [conversations, setConversations] = useState([]);
  const [activeView, setActiveView] = useState('chat'); // 'chat' veya 'database'
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Yeni konuşma başlat
  const startNewConversation = async () => {
    try {
      const response = await axios.post('/api/chat/conversation', {
        userId: 'anonymous'
      });
      
      setConversationId(response.data.conversationId);
      setMessages([]);
    } catch (error) {
      console.error('Konuşma başlatma hatası:', error);
    }
  };

  // Mesaj gönder
  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      role: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await axios.post('/api/chat/send', {
        message: inputMessage,
        conversationId: conversationId || 'temp_' + Date.now(),
        userId: 'anonymous'
      });

      if (response.data.success) {
        const aiMessage = {
          id: response.data.messageId,
          role: 'assistant',
          content: response.data.message,
          timestamp: new Date()
        };

        setMessages(prev => [...prev, aiMessage]);
        
        if (!conversationId) {
          setConversationId(response.data.conversationId);
        }
      }
    } catch (error) {
      console.error('Mesaj gönderme hatası:', error);
      const errorMessage = {
        id: Date.now(),
        role: 'assistant',
        content: 'Üzgünüm, bir hata oluştu. Lütfen tekrar deneyin.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Enter tuşu ile gönder
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Konuşmayı sil
  const deleteConversation = async () => {
    if (!conversationId) return;

    try {
      await axios.delete(`/api/chat/conversation/${conversationId}`);
      setMessages([]);
      setConversationId(null);
    } catch (error) {
      console.error('Konuşma silme hatası:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-primary-600 rounded-lg flex items-center justify-center">
                <Bot className="w-5 h-5 text-white" />
              </div>
              <h1 className="text-xl font-semibold text-gray-900">HukukKem AI Chat</h1>
            </div>
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setActiveView('chat')}
                className={`btn-secondary flex items-center space-x-2 ${
                  activeView === 'chat' ? 'bg-primary-100 text-primary-700' : ''
                }`}
              >
                <MessageSquare className="w-4 h-4" />
                <span>Chat</span>
              </button>
              <button
                onClick={() => setActiveView('database')}
                className={`btn-secondary flex items-center space-x-2 ${
                  activeView === 'database' ? 'bg-primary-100 text-primary-700' : ''
                }`}
              >
                <Database className="w-4 h-4" />
                <span>Veritabanı</span>
              </button>
              {activeView === 'chat' && (
                <>
                  <button
                    onClick={startNewConversation}
                    className="btn-secondary flex items-center space-x-2"
                  >
                    <Plus className="w-4 h-4" />
                    <span>Yeni Konuşma</span>
                  </button>
                  {conversationId && (
                    <button
                      onClick={deleteConversation}
                      className="btn-secondary flex items-center space-x-2 text-red-600 hover:text-red-700"
                    >
                      <Trash2 className="w-4 h-4" />
                      <span>Sil</span>
                    </button>
                  )}
                </>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      {activeView === 'chat' ? (
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 h-[600px] flex flex-col">
            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-6 space-y-4">
              {messages.length === 0 ? (
                <div className="text-center text-gray-500 mt-20">
                  <MessageSquare className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                  <h3 className="text-lg font-medium mb-2">HukukKem AI'a Hoş Geldiniz</h3>
                  <p className="text-sm">
                    Mahkeme kararları ve hukuki konularda sorularınızı sorabilirsiniz.
                  </p>
                </div>
              ) : (
                messages.map((message) => (
                  <div
                    key={message.id}
                    className={`message-bubble ${
                      message.role === 'user' ? 'message-user' : 'message-assistant'
                    }`}
                  >
                    <div className="flex items-start space-x-3">
                      <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                        message.role === 'user' ? 'bg-primary-700' : 'bg-gray-200'
                      }`}>
                        {message.role === 'user' ? (
                          <User className="w-4 h-4 text-white" />
                        ) : (
                          <Bot className="w-4 h-4 text-gray-600" />
                        )}
                      </div>
                      <div className="flex-1">
                        <div className="text-sm font-medium mb-1">
                          {message.role === 'user' ? 'Siz' : 'HukukKem AI'}
                        </div>
                        <div className="text-sm whitespace-pre-wrap">
                          {message.content}
                        </div>
                        <div className="text-xs text-gray-500 mt-2">
                          {message.timestamp.toLocaleTimeString('tr-TR')}
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              )}
              
              {isLoading && (
                <div className="message-bubble message-assistant">
                  <div className="flex items-start space-x-3">
                    <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center">
                      <Bot className="w-4 h-4 text-gray-600" />
                    </div>
                    <div className="flex-1">
                      <div className="text-sm font-medium mb-1">HukukKem AI</div>
                      <div className="flex items-center space-x-2">
                        <Loader2 className="w-4 h-4 animate-spin text-gray-500" />
                        <span className="text-sm text-gray-500">Yanıt hazırlanıyor...</span>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              
              <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <div className="border-t border-gray-200 p-4">
              <div className="flex space-x-4">
                <div className="flex-1">
                  <textarea
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Hukuki sorunuzu buraya yazın..."
                    className="input-field resize-none h-12"
                    rows="1"
                    disabled={isLoading}
                  />
                </div>
                <button
                  onClick={sendMessage}
                  disabled={!inputMessage.trim() || isLoading}
                  className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed flex items-center space-x-2"
                >
                  <Send className="w-4 h-4" />
                  <span>Gönder</span>
                </button>
              </div>
              <div className="text-xs text-gray-500 mt-2">
                Enter tuşu ile gönderebilirsiniz. Shift+Enter ile yeni satır.
              </div>
            </div>
          </div>
        </div>
      ) : (
        <DatabaseViewer />
      )}
    </div>
  );
}

export default App;
