import React, { useState, useEffect } from 'react';
import { Search, Database, BarChart3, FileText, Calendar, Building } from 'lucide-react';
import axios from 'axios';

function DatabaseViewer() {
  const [stats, setStats] = useState(null);
  const [kararlar, setKararlar] = useState([]);
  const [mahkemeler, setMahkemeler] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [activeTab, setActiveTab] = useState('stats');

  useEffect(() => {
    loadStats();
    loadMahkemeler();
  }, []);

  const loadStats = async () => {
    try {
      const response = await axios.get('/api/database/stats');
      setStats(response.data.stats);
    } catch (error) {
      console.error('İstatistik yükleme hatası:', error);
    }
  };

  const loadMahkemeler = async () => {
    try {
      const response = await axios.get('/api/database/mahkemeler');
      setMahkemeler(response.data.mahkemeler);
    } catch (error) {
      console.error('Mahkeme listesi yükleme hatası:', error);
    }
  };

  const loadKararlar = async (limit = 20) => {
    setLoading(true);
    try {
      const response = await axios.get(`/api/database/kararlar?limit=${limit}`);
      setKararlar(response.data.kararlar);
    } catch (error) {
      console.error('Kararlar yükleme hatası:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    setLoading(true);
    try {
      const response = await axios.get(`/api/database/search?q=${encodeURIComponent(searchQuery)}&limit=10`);
      setSearchResults(response.data.results);
      setActiveTab('search');
    } catch (error) {
      console.error('Arama hatası:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('tr-TR');
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Veritabanı Yönetimi</h1>
        <p className="text-gray-600">Mahkeme kararları ve chunk'ları görüntüleyin</p>
      </div>

      {/* Tab Navigation */}
      <div className="border-b border-gray-200 mb-6">
        <nav className="-mb-px flex space-x-8">
          <button
            onClick={() => setActiveTab('stats')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'stats'
                ? 'border-primary-500 text-primary-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            <BarChart3 className="w-4 h-4 inline mr-2" />
            İstatistikler
          </button>
          <button
            onClick={() => setActiveTab('search')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'search'
                ? 'border-primary-500 text-primary-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            <Search className="w-4 h-4 inline mr-2" />
            Arama
          </button>
          <button
            onClick={() => setActiveTab('kararlar')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'kararlar'
                ? 'border-primary-500 text-primary-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            <FileText className="w-4 h-4 inline mr-2" />
            Kararlar
          </button>
          <button
            onClick={() => setActiveTab('mahkemeler')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'mahkemeler'
                ? 'border-primary-500 text-primary-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            <Building className="w-4 h-4 inline mr-2" />
            Mahkemeler
          </button>
        </nav>
      </div>

      {/* Stats Tab */}
      {activeTab === 'stats' && stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Database className="w-8 h-8 text-primary-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Toplam Karar</p>
                <p className="text-2xl font-semibold text-gray-900">{stats.toplam_karar}</p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Building className="w-8 h-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Toplam Mahkeme</p>
                <p className="text-2xl font-semibold text-gray-900">{stats.toplam_mahkeme}</p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <FileText className="w-8 h-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Toplam Chunk</p>
                <p className="text-2xl font-semibold text-gray-900">{stats.toplam_chunk}</p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Calendar className="w-8 h-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">En Yeni Karar</p>
                <p className="text-lg font-semibold text-gray-900">{formatDate(stats.en_yeni_karar)}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Search Tab */}
      {activeTab === 'search' && (
        <div>
          <div className="mb-6">
            <div className="flex space-x-4">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Mahkeme kararlarında arama yapın..."
                className="flex-1 input-field"
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              />
              <button
                onClick={handleSearch}
                disabled={loading}
                className="btn-primary flex items-center space-x-2"
              >
                <Search className="w-4 h-4" />
                <span>Ara</span>
              </button>
            </div>
          </div>

          {searchResults.length > 0 && (
            <div className="space-y-4">
              <h3 className="text-lg font-medium text-gray-900">
                Arama Sonuçları ({searchResults.length})
              </h3>
              {searchResults.map((result) => (
                <div key={result.id} className="bg-white rounded-lg shadow p-6">
                  <h4 className="text-lg font-semibold text-gray-900 mb-2">{result.baslik}</h4>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-gray-600 mb-3">
                    <div><strong>Mahkeme:</strong> {result.mahkeme}</div>
                    <div><strong>Tarih:</strong> {formatDate(result.karar_tarihi)}</div>
                    <div><strong>Başvuru No:</strong> {result.basvuru_no}</div>
                    <div><strong>Başvurucu:</strong> {result.basvurucu}</div>
                  </div>
                  <p className="text-gray-700">{result.karar_ozeti}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Kararlar Tab */}
      {activeTab === 'kararlar' && (
        <div>
          <div className="mb-6 flex justify-between items-center">
            <h3 className="text-lg font-medium text-gray-900">Son Kararlar</h3>
            <button
              onClick={() => loadKararlar(20)}
              disabled={loading}
              className="btn-secondary"
            >
              {loading ? 'Yükleniyor...' : 'Yenile'}
            </button>
          </div>

          {kararlar.length > 0 && (
            <div className="space-y-4">
              {kararlar.map((karar) => (
                <div key={karar.id} className="bg-white rounded-lg shadow p-6">
                  <h4 className="text-lg font-semibold text-gray-900 mb-2">{karar.baslik}</h4>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm text-gray-600 mb-3">
                    <div><strong>Mahkeme:</strong> {karar.mahkeme}</div>
                    <div><strong>Tarih:</strong> {formatDate(karar.karar_tarihi)}</div>
                    <div><strong>Başvuru No:</strong> {karar.basvuru_no}</div>
                    <div><strong>Başvurucu:</strong> {karar.basvurucu}</div>
                  </div>
                  <p className="text-gray-700">{karar.karar_ozeti}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Mahkemeler Tab */}
      {activeTab === 'mahkemeler' && (
        <div>
          <h3 className="text-lg font-medium text-gray-900 mb-6">Mahkeme Listesi</h3>
          <div className="bg-white rounded-lg shadow overflow-hidden">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Mahkeme Adı
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Karar Sayısı
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {mahkemeler.map((mahkeme, index) => (
                  <tr key={index}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {mahkeme.mahkeme}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {mahkeme.karar_sayisi}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default DatabaseViewer;
