import DatabaseViewer from './components/DatabaseViewer';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversationId, setConversationId] = useState(null);
  const [conversations, setConversations] = useState([]);
  const [activeView, setActiveView] = useState('chat'); // 'chat' veya 'database'
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [loadingConversations, setLoadingConversations] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Yeni konuşma başlat
  const startNewConversation = async () => {
    try {
      const response = await axios.post('/api/chat/conversation', {
        userId: 'anonymous'
      });
      
      setConversationId(response.data.conversationId);
      setMessages([]);
    } catch (error) {
      console.error('Konuşma başlatma hatası:', error);
    }
  };

  // Mesaj gönder
  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      role: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await axios.post('/api/chat/send', {
        message: inputMessage,
        conversationId: conversationId || 'temp_' + Date.now(),
        userId: 'anonymous'
      });

      if (response.data.success) {
        const aiMessage = {
          id: response.data.messageId,
          role: 'assistant',
          content: response.data.message,
          timestamp: new Date()
        };

        setMessages(prev => [...prev, aiMessage]);
        
        if (!conversationId) {
          setConversationId(response.data.conversationId);
        }
      }
    } catch (error) {
      console.error('Mesaj gönderme hatası:', error);
      const errorMessage = {
        id: Date.now(),
        role: 'assistant',
        content: 'Üzgünüm, bir hata oluştu. Lütfen tekrar deneyin.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Enter tuşu ile gönder
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Konuşmayı sil
  const deleteConversation = async () => {
    if (!conversationId) return;

    try {
      await axios.delete(`/api/chat/conversation/${conversationId}`);
      setMessages([]);
      setConversationId(null);
    } catch (error) {
      console.error('Konuşma silme hatası:', error);
    }
  };

  return (
    <div className="h-screen bg-gray-900 flex flex-col">
      {/* Header */}
      <header className="bg-gray-800 border-b border-gray-700 h-16 flex items-center justify-between px-6">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
            <Bot className="w-5 h-5 text-white" />
          </div>
          <h1 className="text-xl font-semibold text-white">HukukKem AI Chat</h1>
        </div>
        <div className="flex items-center space-x-2">
          <button
            onClick={() => setActiveView('chat')}
            className={`px-3 py-2 rounded-lg flex items-center space-x-2 transition-colors ${
              activeView === 'chat' 
                ? 'bg-blue-600 text-white' 
                : 'text-gray-300 hover:text-white hover:bg-gray-700'
            }`}
          >
            <MessageSquare className="w-4 h-4" />
            <span>Chat</span>
          </button>
          <button
            onClick={() => setActiveView('database')}
            className={`px-3 py-2 rounded-lg flex items-center space-x-2 transition-colors ${
              activeView === 'database' 
                ? 'bg-blue-600 text-white' 
                : 'text-gray-300 hover:text-white hover:bg-gray-700'
            }`}
          >
            <Database className="w-4 h-4" />
            <span>Veritabanı</span>
          </button>
        </div>
      </header>

      {/* Main Content */}
      {activeView === 'chat' ? (
        <div className="flex-1 flex">
          {/* Sidebar - Eski Sohbetler */}
          <div className={`bg-gray-800 border-r border-gray-700 transition-all duration-300 ${
            sidebarOpen ? 'w-80' : 'w-0'
          } overflow-hidden`}>
            <div className="p-4">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-semibold text-white">Eski Sohbetler</h2>
                <button
                  onClick={startNewConversation}
                  className="p-2 text-gray-300 hover:text-white hover:bg-gray-700 rounded-lg transition-colors"
                >
                  <Plus className="w-4 h-4" />
                </button>
              </div>
              
              {/* Örnek eski sohbetler */}
              <div className="space-y-2">
                {[
                  { id: 1, title: 'İş hukuku soruları', date: '2024-01-15', messages: 8 },
                  { id: 2, title: 'Ticaret hukuku danışmanlığı', date: '2024-01-14', messages: 12 },
                  { id: 3, title: 'Aile hukuku konuları', date: '2024-01-13', messages: 5 },
                  { id: 4, title: 'Ceza hukuku analizi', date: '2024-01-12', messages: 15 },
                ].map((conv) => (
                  <div
                    key={conv.id}
                    className="p-3 bg-gray-700 rounded-lg cursor-pointer hover:bg-gray-600 transition-colors"
                  >
                    <div className="text-white font-medium text-sm">{conv.title}</div>
                    <div className="flex items-center justify-between mt-1">
                      <div className="text-gray-400 text-xs">{conv.date}</div>
                      <div className="text-gray-400 text-xs">{conv.messages} mesaj</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Chat Area */}
          <div className="flex-1 flex flex-col bg-gray-900">
            {/* Chat Header */}
            <div className="bg-gray-800 border-b border-gray-700 p-4 flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                  className="p-2 text-gray-300 hover:text-white hover:bg-gray-700 rounded-lg transition-colors"
                >
                  <ChevronLeft className={`w-4 h-4 transition-transform ${sidebarOpen ? 'rotate-180' : ''}`} />
                </button>
                <div>
                  <h3 className="text-white font-medium">
                    {conversationId ? 'Mevcut Konuşma' : 'Yeni Konuşma'}
                  </h3>
                  <p className="text-gray-400 text-sm">
                    {messages.length} mesaj
                  </p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <button
                  onClick={startNewConversation}
                  className="px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
                >
                  <Plus className="w-4 h-4" />
                  <span>Yeni</span>
                </button>
                {conversationId && (
                  <button
                    onClick={deleteConversation}
                    className="px-3 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors flex items-center space-x-2"
                  >
                    <Trash2 className="w-4 h-4" />
                    <span>Sil</span>
                  </button>
                )}
              </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-6 space-y-4">
              {messages.length === 0 ? (
                <div className="text-center text-gray-400 mt-20">
                  <MessageSquare className="w-16 h-16 mx-auto mb-4 text-gray-600" />
                  <h3 className="text-xl font-medium mb-2 text-white">HukukKem AI'a Hoş Geldiniz</h3>
                  <p className="text-gray-400">
                    Mahkeme kararları ve hukuki konularda sorularınızı sorabilirsiniz.
                  </p>
                </div>
              ) : (
                messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className={`max-w-3xl ${message.role === 'user' ? 'order-2' : 'order-1'}`}>
                      <div className={`flex items-start space-x-3 ${
                        message.role === 'user' ? 'flex-row-reverse space-x-reverse' : ''
                      }`}>
                        <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                          message.role === 'user' ? 'bg-blue-600' : 'bg-gray-600'
                        }`}>
                          {message.role === 'user' ? (
                            <User className="w-4 h-4 text-white" />
                          ) : (
                            <Bot className="w-4 h-4 text-white" />
                          )}
                        </div>
                        <div className={`rounded-lg px-4 py-3 ${
                          message.role === 'user' 
                            ? 'bg-blue-600 text-white' 
                            : 'bg-gray-700 text-white'
                        }`}>
                          <div className="text-sm whitespace-pre-wrap">
                            {message.content}
                          </div>
                          <div className={`text-xs mt-2 ${
                            message.role === 'user' ? 'text-blue-200' : 'text-gray-400'
                          }`}>
                            {message.timestamp.toLocaleTimeString('tr-TR')}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              )}
              
              {isLoading && (
                <div className="flex justify-start">
                  <div className="max-w-3xl">
                    <div className="flex items-start space-x-3">
                      <div className="w-8 h-8 bg-gray-600 rounded-full flex items-center justify-center flex-shrink-0">
                        <Bot className="w-4 h-4 text-white" />
                      </div>
                      <div className="bg-gray-700 rounded-lg px-4 py-3">
                        <div className="flex items-center space-x-2">
                          <Loader2 className="w-4 h-4 animate-spin text-gray-400" />
                          <span className="text-sm text-gray-400">Yanıt hazırlanıyor...</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              
              <div ref={messagesEndRef} />
            </div>

            {/* Input - Sabit Alt */}
            <div className="bg-gray-800 border-t border-gray-700 p-4">
              <div className="flex space-x-4">
                <div className="flex-1">
                  <textarea
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Hukuki sorunuzu buraya yazın..."
                    className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    rows="1"
                    disabled={isLoading}
                  />
                </div>
                <button
                  onClick={sendMessage}
                  disabled={!inputMessage.trim() || isLoading}
                  className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center space-x-2"
                >
                  <Send className="w-4 h-4" />
                  <span>Gönder</span>
                </button>
              </div>
              <div className="text-xs text-gray-400 mt-2">
                Enter tuşu ile gönderebilirsiniz. Shift+Enter ile yeni satır.
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="flex-1">
          <DatabaseViewer />
        </div>
      )}
    </div>
  );
}

export default App;
