import React, { useState, useEffect, useRef } from 'react';
import { Send, Bot, User, Loader2, MessageSquare, Plus, Trash2, Database, Clock, ChevronLeft } from 'lucide-react';
import axios from 'axios';
import DatabaseViewer from './components/DatabaseViewer';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversationId, setConversationId] = useState(null);
  const [conversations, setConversations] = useState([]);
  const [activeView, setActiveView] = useState('chat'); // 'chat' veya 'database'
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [loadingConversations, setLoadingConversations] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // KonuÅŸmalarÄ± yÃ¼kle
  useEffect(() => {
    if (activeView === 'chat') {
      loadConversations();
    }
  }, [activeView]);

  const loadConversations = async () => {
    setLoadingConversations(true);
    try {
      const response = await axios.get('/api/chat/conversations?userId=anonymous');
      if (response.data.success) {
        setConversations(response.data.conversations);
      }
    } catch (error) {
      console.error('KonuÅŸmalar yÃ¼klenemedi:', error);
    } finally {
      setLoadingConversations(false);
    }
  };

  // KonuÅŸma seÃ§
  const selectConversation = async (conversationId) => {
    try {
      const response = await axios.get(`/api/chat/conversation/${conversationId}`);
      if (response.data.success) {
        setConversationId(conversationId);
        setMessages(response.data.messages.map(msg => ({
          id: msg.id,
          role: msg.role,
          content: msg.content,
          timestamp: new Date(msg.created_at)
        })));
      }
    } catch (error) {
      console.error('KonuÅŸma yÃ¼klenemedi:', error);
    }
  };

  // Yeni konuÅŸma baÅŸlat
  const startNewConversation = async () => {
    try {
      const response = await axios.post('/api/chat/conversation', {
        userId: 'anonymous'
      });
      
      setConversationId(response.data.conversationId);
      setMessages([]);
      loadConversations(); // KonuÅŸma listesini yenile
    } catch (error) {
      console.error('KonuÅŸma baÅŸlatma hatasÄ±:', error);
    }
  };

  // Mesaj gÃ¶nder
  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      role: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await axios.post('/api/chat/send', {
        message: inputMessage,
        conversationId: conversationId || 'temp_' + Date.now(),
        userId: 'anonymous'
      });

      if (response.data.success) {
        const aiMessage = {
          id: response.data.messageId,
          role: 'assistant',
          content: response.data.message,
          timestamp: new Date()
        };

        setMessages(prev => [...prev, aiMessage]);
        
        if (!conversationId) {
          setConversationId(response.data.conversationId);
        }
        
        // KonuÅŸma listesini yenile
        loadConversations();
      }
    } catch (error) {
      console.error('Mesaj gÃ¶nderme hatasÄ±:', error);
      const errorMessage = {
        id: Date.now(),
        role: 'assistant',
        content: 'ÃœzgÃ¼nÃ¼m, bir hata oluÅŸtu. LÃ¼tfen tekrar deneyin.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Enter tuÅŸu ile gÃ¶nder
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // KonuÅŸmayÄ± sil
  const deleteConversation = async () => {
    if (!conversationId) return;

    try {
      await axios.delete(`/api/chat/conversation/${conversationId}`);
      setMessages([]);
      setConversationId(null);
      loadConversations(); // KonuÅŸma listesini yenile
    } catch (error) {
      console.error('KonuÅŸma silme hatasÄ±:', error);
    }
  };

  // Test sohbetleri ekle
  const addTestConversations = async () => {
    try {
      await axios.post('/api/chat/add-test-conversations', {
        userId: 'anonymous'
      });
      loadConversations(); // KonuÅŸma listesini yenile
      alert('100 test sohbeti eklendi!');
    } catch (error) {
      console.error('Test sohbeti ekleme hatasÄ±:', error);
      alert('Test sohbetleri eklenirken hata oluÅŸtu!');
    }
  };

  return (
    <div className="h-screen bg-gray-900 flex flex-col overflow-hidden">
      {/* Header - Sabit Ãœst */}
      <header className="bg-gray-800 border-b border-gray-700 h-16 flex items-center justify-between px-6 flex-shrink-0">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
            <Bot className="w-5 h-5 text-white" />
          </div>
          <h1 className="text-xl font-semibold text-white">HukukKem AI Chat</h1>
        </div>
        <div className="flex items-center space-x-2">
          <button
            onClick={() => setActiveView('chat')}
            className={`px-3 py-2 rounded-lg flex items-center space-x-2 transition-colors ${
              activeView === 'chat' 
                ? 'bg-blue-600 text-white' 
                : 'text-gray-300 hover:text-white hover:bg-gray-700'
            }`}
          >
            <MessageSquare className="w-4 h-4" />
            <span>Chat</span>
          </button>
          <button
            onClick={() => setActiveView('database')}
            className={`px-3 py-2 rounded-lg flex items-center space-x-2 transition-colors ${
              activeView === 'database' 
                ? 'bg-blue-600 text-white' 
                : 'text-gray-300 hover:text-white hover:bg-gray-700'
            }`}
          >
            <Database className="w-4 h-4" />
            <span>VeritabanÄ±</span>
          </button>
        </div>
      </header>

      {/* Main Content */}
      {activeView === 'chat' ? (
        <div className="flex-1 flex overflow-hidden">
          {/* Sidebar - Eski Sohbetler - Sabit */}
          <div className={`bg-gray-800 border-r border-gray-700 transition-all duration-300 flex-shrink-0 ${
            sidebarOpen ? 'w-80' : 'w-0'
          } overflow-hidden`}>
            <div className="p-4 h-full flex flex-col">
              <div className="flex items-center justify-between mb-4 flex-shrink-0">
                <h2 className="text-lg font-semibold text-white">Eski Sohbetler</h2>
                <button
                  onClick={startNewConversation}
                  className="p-2 text-gray-300 hover:text-white hover:bg-gray-700 rounded-lg transition-colors"
                >
                  <Plus className="w-4 h-4" />
                </button>
              </div>
              
              {/* GerÃ§ek eski sohbetler - Scroll */}
              <div className="flex-1 overflow-y-auto space-y-2 scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800 hover:scrollbar-thumb-gray-500">
                {loadingConversations ? (
                  <div className="flex items-center justify-center py-4">
                    <Loader2 className="w-4 h-4 animate-spin text-gray-400" />
                    <span className="text-gray-400 text-sm ml-2">YÃ¼kleniyor...</span>
                  </div>
                ) : conversations.length === 0 ? (
                  <div className="text-center text-gray-400 py-4">
                    <MessageSquare className="w-8 h-8 mx-auto mb-2 text-gray-600" />
                    <p className="text-sm">HenÃ¼z sohbet yok</p>
                  </div>
                ) : (
                  conversations.map((conv) => (
                    <div
                      key={conv.id}
                      onClick={() => selectConversation(conv.id)}
                      className={`p-3 rounded-lg cursor-pointer transition-colors ${
                        conversationId === conv.id 
                          ? 'bg-blue-600 text-white' 
                          : 'bg-gray-700 hover:bg-gray-600 text-white'
                      }`}
                    >
                      <div className="font-medium text-sm truncate">{conv.title}</div>
                      <div className="flex items-center justify-between mt-1">
                        <div className={`text-xs ${
                          conversationId === conv.id ? 'text-blue-200' : 'text-gray-400'
                        }`}>
                          {new Date(conv.updated_at).toLocaleDateString('tr-TR')}
                        </div>
                        <div className={`text-xs ${
                          conversationId === conv.id ? 'text-blue-200' : 'text-gray-400'
                        }`}>
                          {conv.message_count} mesaj
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>

          {/* Chat Area */}
          <div className="flex-1 flex flex-col bg-gray-900 overflow-hidden">
            {/* Chat Header - Sabit */}
            <div className="bg-gray-800 border-b border-gray-700 p-4 flex items-center justify-between flex-shrink-0">
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                  className="p-2 text-gray-300 hover:text-white hover:bg-gray-700 rounded-lg transition-colors"
                >
                  <ChevronLeft className={`w-4 h-4 transition-transform ${sidebarOpen ? 'rotate-180' : ''}`} />
                </button>
                <div>
                  <h3 className="text-white font-medium">
                    {conversationId ? 'Mevcut KonuÅŸma' : 'Yeni KonuÅŸma'}
                  </h3>
                  <p className="text-gray-400 text-sm">
                    {messages.length} mesaj
                  </p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <button
                  onClick={startNewConversation}
                  className="px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
                >
                  <Plus className="w-4 h-4" />
                  <span>Yeni</span>
                </button>
                <button
                  onClick={addTestConversations}
                  className="px-3 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors flex items-center space-x-2"
                >
                  <span>ðŸ§ª Test 100</span>
                </button>
                {conversationId && (
                  <button
                    onClick={deleteConversation}
                    className="px-3 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors flex items-center space-x-2"
                  >
                    <Trash2 className="w-4 h-4" />
                    <span>Sil</span>
                  </button>
                )}
              </div>
            </div>

            {/* Messages - Sadece Bu KÄ±sÄ±m Scroll */}
            <div className="flex-1 overflow-y-auto p-6 space-y-4 scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-900 hover:scrollbar-thumb-gray-500">
              {messages.length === 0 ? (
                <div className="text-center text-gray-400 mt-20">
                  <MessageSquare className="w-16 h-16 mx-auto mb-4 text-gray-600" />
                  <h3 className="text-xl font-medium mb-2 text-white">HukukKem AI'a HoÅŸ Geldiniz</h3>
                  <p className="text-gray-400">
                    Mahkeme kararlarÄ± ve hukuki konularda sorularÄ±nÄ±zÄ± sorabilirsiniz.
                  </p>
                </div>
              ) : (
                messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className={`max-w-3xl ${message.role === 'user' ? 'order-2' : 'order-1'}`}>
                      <div className={`flex items-start space-x-3 ${
                        message.role === 'user' ? 'flex-row-reverse space-x-reverse' : ''
                      }`}>
                        <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                          message.role === 'user' ? 'bg-blue-600' : 'bg-gray-600'
                        }`}>
                          {message.role === 'user' ? (
                            <User className="w-4 h-4 text-white" />
                          ) : (
                            <Bot className="w-4 h-4 text-white" />
                          )}
                        </div>
                        <div className={`rounded-lg px-4 py-3 ${
                          message.role === 'user' 
                            ? 'bg-blue-600 text-white' 
                            : 'bg-gray-700 text-white'
                        }`}>
                          <div className="text-sm whitespace-pre-wrap">
                            {message.content}
                          </div>
                          <div className={`text-xs mt-2 ${
                            message.role === 'user' ? 'text-blue-200' : 'text-gray-400'
                          }`}>
                            {message.timestamp.toLocaleTimeString('tr-TR')}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              )}
              
              {isLoading && (
                <div className="flex justify-start">
                  <div className="max-w-3xl">
                    <div className="flex items-start space-x-3">
                      <div className="w-8 h-8 bg-gray-600 rounded-full flex items-center justify-center flex-shrink-0">
                        <Bot className="w-4 h-4 text-white" />
                      </div>
                      <div className="bg-gray-700 rounded-lg px-4 py-3">
                        <div className="flex items-center space-x-2">
                          <Loader2 className="w-4 h-4 animate-spin text-gray-400" />
                          <span className="text-sm text-gray-400">YanÄ±t hazÄ±rlanÄ±yor...</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              
              <div ref={messagesEndRef} />
            </div>

            {/* Input - Viewport'a Sabit Alt */}
            <div className="bg-gray-800 border-t border-gray-700 p-4 flex-shrink-0" style={{ height: '100px', minHeight: '100px', maxHeight: '100px', position: 'sticky', bottom: 0 }}>
              <div className="flex space-x-4 h-full">
                <div className="flex-1 h-full">
                  <textarea
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Hukuki sorunuzu buraya yazÄ±n..."
                    className="w-full h-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-3 text-white placeholder-gray-400 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    disabled={isLoading}
                  />
                </div>
                <button
                  onClick={sendMessage}
                  disabled={!inputMessage.trim() || isLoading}
                  className="bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center justify-center space-x-2 flex-shrink-0"
                  style={{ width: '100px' }}
                >
                  <Send className="w-4 h-4" />
                  <span>GÃ¶nder</span>
                </button>
              </div>
              <div className="text-xs text-gray-400 mt-1">
                Enter tuÅŸu ile gÃ¶nderebilirsiniz. Shift+Enter ile yeni satÄ±r.
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="flex-1">
          <DatabaseViewer />
        </div>
      )}
    </div>
  );
}

export default App;
